#include <stdio.h>#include <stdlib.h>    // for exit()#include <string.h>    // for strcpy(), strcat()#define LEN 40int main(int argc, char *argv[]){	FILE  *in, *out;	int ch;	char name[LEN];	int count = 0;	// 检查命令行参数 	if (argc < 2)	{		fprintf(stderr, "Usage: %s filename\n", argv[0]);		exit(1);//终止程序	}	// set up input，argv[1]中保存了要打开的文件名	if ((in = fopen(argv[1], "r")) == NULL)	{		fprintf(stderr, "I couldn't open the file \"%s\"\n",			argv[1]);		exit(2);	}	// set up output	strcpy(name, argv[1]); // copy filename	strcat(name, ".red");            // append .red	if ((out = fopen(name, "w")) == NULL)	{                       // open file for writing		fprintf(stderr, "Can't create output file.\n");		exit(3);	}